operation.add:
/* NeoSeo Backup - begin */
$data['neoseo_backup'] = false;
if ($this->user->hasPermission('access','tool/neoseo_backup') && isset($this->session->data['token'])) {
	$this->language->load("tool/neoseo_backup");
	if ($this->config->get("neoseo_backup_status")) {
		if ($this->config->get("neoseo_backup_replace_system_backup")) {
			$data['text_backup'] = $this->language->get("text_neoseo_backup");
			$data['backup'] = $this->url->link('tool/neoseo_backup', 'token='.$this->session->data['token'], 'SSL');
		} else {
			$data['text_neoseo_backup'] = $this->language->get("text_neoseo_backup");
			$data['neoseo_backup'] = $this->url->link('tool/neoseo_backup', 'token='.$this->session->data['token'], 'SSL');
		}
	}
}
/* NeoSeo Backup - begin */
