operation.add:
/* NeoSeo Filter Page Generator - begin */
$data['filter_page_generator_status'] = $this->config->get('neoseo_filter_page_generator_status');	
if($data['filter_page_generator_status'] == 1 && $this->user->hasPermission('access','catalog/neoseo_filter_page_generator') && isset($this->session->data['token']) ) {
	$this->language->load("catalog/neoseo_filter_page_generator_menu");
	$data['text_filter_page_generator_menu'] = $this->language->get("text_filter_page_generator_menu");
	$data['link_filter_page_generator_menu'] = $this->url->link('catalog/neoseo_filter_page_generator', 'token=' . $this->session->data['token'], 'SSL');
}
/* NeoSeo Filter Page Generator - end */
