		$data['neoseo_price_import_status'] = $this->config->get("neoseo_price_import_status");
		$data['text_neoseo_price_import'] = $this->language->get("text_neoseo_price_import");
		$data['neoseo_price_import'] = $this->url->link('tool/neoseo_price_import', 'token=' . $this->session->data['token'], 'SSL');
	}
}
/* NeoSeo Price Import - end */

